  `Create_time` datetime DEFAULT NULL,
  `Modify_time` datetime DEFAULT NULL,
  `EDGE_Group` varchar(255) DEFAULT NULL,
  `Type` enum('EDGE','Potential','Extinct','Other') CHARACTER SET latin1 DEFAULT NULL,
  `EDGE_rank` varchar(255) DEFAULT NULL,
  `Species_class` varchar(255) DEFAULT NULL,
  `Species_order` varchar(255) DEFAULT NULL,
  `Species_family` varchar(255) DEFAULT NULL,
  `Scientific_name` varchar(255) DEFAULT NULL,
  `Synonym` longtext,
  `Common_name` varchar(255) DEFAULT NULL,
  `ED_score` varchar(255) DEFAULT NULL,
  `GE_score` varchar(255) DEFAULT NULL,
  `EDGE_score` varchar(255) DEFAULT NULL,
  `Short_summary` longtext,
  `Summary` longtext,
  `Other_names` longtext,
  `Edgeometer` varchar(255) DEFAULT NULL,
  `Possibly_extinct` enum('N','Y') CHARACTER SET latin1 NOT NULL DEFAULT 'N',
