  `Search_countries` longtext,
  `Generate_countries` longtext,
  `Aquatic_regions` longtext,
  PRIMARY KEY (`Species_ID`,`Status`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2048 ;

